import imports
from imports import *
import config

def exclude():
    return False

def onEnter(self):
    pass

def initPanel(self):

    self.seedsAItem = AccordionItem(title='Seeds & Complex', background_selected= os.sep + 'resources' + os.sep + "ui_images" + os.sep + 'invisible.png', min_space=config.aiheight)

    self.seedsMainBox = BoxLayout(orientation='vertical')

    self.seedsMainBox.add_widget(Label(text="Mythic Style", size_hint=(1,.1)))

    twoPartSeedList = [ 'fairy tale', 'sf horror', 'hardboiled mystery', 'medieval romance' ]

    for seedpack in twoPartSeedList:

        button = Button(text=seedpack, size_hint=(1,.20), background_normal='', background_color=neutral, background_down='', background_color_down=neutral, font_name='Fantasque-Sans')
        button.self = self
        button.bind(on_press=self.pressGenericButton)
        button.bind(on_release=useTwoPartSeed)
        self.seedsMainBox.add_widget(button)

    self.seedsMainBox.add_widget(Label(text="Location Crafter Style", size_hint=(1,.1)))

    threePartSeedList = [ 'hardboiled mystery', 'sf horror', 'medieval romance' ]

    for seedpack in threePartSeedList:

        button = Button(text=seedpack + " description", size_hint=(1,.20), background_normal='', background_color=neutral, background_down='', background_color_down=neutral, font_name='Fantasque-Sans')
        button.self = self
        button.bind(on_press=self.pressGenericButton)
        button.bind(on_release=useThreePartSeed)
        self.seedsMainBox.add_widget(button)

        button = Button(text=seedpack + " verb", size_hint=(1,.20), background_normal='', background_color=neutral, background_down='', background_color_down=neutral, font_name='Fantasque-Sans')
        button.self = self
        button.bind(on_press=self.pressGenericButton)
        button.bind(on_release=useThreePartSeed)
        self.seedsMainBox.add_widget(button)

    allSeedList = [ 'medieval romance' ]

    self.seedsMainBox.add_widget(Label(text="One Big List", size_hint=(1,.1)))

    for seedpack in allSeedList:

        button = Button(text=seedpack, size_hint=(1,.20), background_normal='', background_color=neutral, background_down='', background_color_down=neutral, font_name='Fantasque-Sans')
        button.self = self
        button.bind(on_press=self.pressGenericButton)
        button.bind(on_release=useSingleSeed)
        self.seedsMainBox.add_widget(button)

    self.seedsAItem.add_widget(self.seedsMainBox)

    return self.seedsAItem

def useTwoPartSeed(*args):
    try:
        self = args[0].self
        args[0].background_color = neutral

        title = args[0].text
    except:
        self = args[0]
        title = args[1]

    title = title.replace(' ', '_')

    result = eval('seed_' + title + '_action')() + " " + eval('seed_' + title + '_subject')()

    updateCenterDisplay(self, "[Seed] " + result, 'oracle')

def useThreePartSeed(*args):

    try:
        self = args[0].self
        args[0].background_color = neutral

        title = args[0].text
    except:
        self = args[0]
        title = args[1]

    title = title.replace(' ', '_')

    if 'description' in title:
        result = eval('seed_' + title)() + " " + eval('seed_' + title.replace('description', 'subject'))()
    else:
        result = eval('seed_' + title.replace('verb', 'description'))() + " " + eval('seed_' + title)()

    updateCenterDisplay(self, "[Seed] " + result, 'oracle')

def useSingleSeed(*args):

    try:
        self = args[0].self
        args[0].background_color = neutral

        title = args[0].text
    except:
        self = args[0]
        title = args[1]

    title = title.replace(' ', '_')

    result = eval('seed_' + title + '_all')()

    updateCenterDisplay(self, "[Seed] " + result, 'oracle')

